   import React, { Component } from 'react'

import {Navbar, Form, FormControl, 
  Dropdown,
  Container,
  DropdownButton, ListGroup,
  Popover, OverlayTrigger,
  InputGroup} from 'react-bootstrap';

export default class Search extends Component {


constructor(props) {
    super(props);


  this.state = {
      jenis: 'Semua Jenis',
      alamat: 'Semua',
  }
}

   _handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      console.log('Proses Mencari');
    }
  }

   render() {
    const popover = (
  <Popover id="popover-basic" title="Pilih Lokasi">
  <ListGroup variant="flush" style={{}}>
              <ListGroup.Item onClick={() => this.setState({alamat:'Yogyakarta'},
               console.log(this.state.alamat)
              )}>Yogyakarta</ListGroup.Item>
              <ListGroup.Item onClick={() => this.setState({alamat:'KulonProgo'},
               console.log(this.state.alamat)
                )}>KulonProgo</ListGroup.Item>
              <ListGroup.Item onClick={() => this.setState({alamat:'Bantul'},
               console.log(this.state.alamat)
                )}>Bantul</ListGroup.Item>
            </ListGroup>
  </Popover>
);
    return (
   <Container style={{marginBottom:-20}}>
              <Navbar.Brand style={{color: 'white'}}>
              <b>
              Proday
              </b>
              </Navbar.Brand>
              <Navbar bg="none" variant="dark">
                  <Form inline>
                      <InputGroup className="mb-3" style={{marginRight:10}}>
                          <InputGroup.Prepend>
                              <InputGroup.Text id="basic-addon1" style={{backgroundColor: 'white'}}>@</InputGroup.Text>
                          </InputGroup.Prepend>
                          <OverlayTrigger trigger="focus" placement="bottom" overlay={popover}>
                          <FormControl placeholder={"Pilih Lokasi "+this.state.alamat} aria-label="Username" aria-describedby="basic-addon1" />
                          </OverlayTrigger>
                      </InputGroup>

                      <InputGroup className="mb-3" style={{marginRight:10}}>
                          <InputGroup.Prepend>
                              <InputGroup.Text id="basic-addon1" style={{backgroundColor: 'white'}}>@</InputGroup.Text>
                          </InputGroup.Prepend>
                          <FormControl placeholder="Kategori" aria-label="Kategori" aria-describedby="basic-addon1" />
                      </InputGroup>

                      <InputGroup className="mb-3" style={{marginRight:10}}>
                          <InputGroup.Prepend>
                              <InputGroup.Text id="basic-addon1" style={{backgroundColor: 'white'}}></InputGroup.Text>
                          </InputGroup.Prepend>

                        <DropdownButton as={InputGroup.Append} style={{marginLeft:-22, paddingRigth:30}} variant="light" title={this.state.jenis} id="input-group-dropdown-2">
                          <Dropdown.Item eventKey='jual' onClick={() => this.setState({jenis:'Dijual'})}
                          >Jual</Dropdown.Item>
                              <Dropdown.Divider />
                              <Dropdown.Item eventKey='sewa' onClick={() => this.setState({jenis:'Sewa'})}>Sewa</Dropdown.Item>
                        </DropdownButton>

                      </InputGroup>

                      <InputGroup className="mb-3" style={{marginRight:10}}>
                          <InputGroup.Prepend>
                              <InputGroup.Text id="basic-addon1" style={{backgroundColor: 'white'}}>@</InputGroup.Text>
                          </InputGroup.Prepend>
                          <FormControl placeholder="Cari Kata Kunci.." aria-label="Username" onKeyDown={this._handleKeyDown}  aria-describedby="basic-addon1" />
                      </InputGroup>

                  </Form>
              </Navbar>          
            </Container>
            )
  }
}
